{"filter":false,"title":"views.py","tooltip":"/cart/views.py","undoManager":{"mark":1,"position":1,"stack":[[{"start":{"row":2,"column":0},"end":{"row":3,"column":0},"action":"remove","lines":["# Create your views here.",""],"id":2},{"start":{"row":2,"column":0},"end":{"row":35,"column":41},"action":"insert","lines":["from django.shortcuts import render, redirect, reverse","","# Create your views here.","def view_cart(request):","    \"\"\"A View that renders the cart contents page\"\"\"","    return render(request, \"cart.html\")","","","def add_to_cart(request, id):","    \"\"\"Add a quantity of the specified product to the cart\"\"\"","    quantity = int(request.POST.get('quantity'))","","    cart = request.session.get('cart', {})","    cart[id] = cart.get(id, quantity)","","    request.session['cart'] = cart","    return redirect(reverse('index'))","","","def adjust_cart(request, id):","    \"\"\"","    Adjust the quantity of the specified product to the specified","    amount","    \"\"\"","    quantity = int(request.POST.get('quantity'))","    cart = request.session.get('cart', {})","","    if quantity > 0:","        cart[id] = quantity","    else:","        cart.pop(id)","    ","    request.session['cart'] = cart","    return redirect(reverse('view_cart'))"]}],[{"start":{"row":0,"column":0},"end":{"row":0,"column":35},"action":"remove","lines":["from django.shortcuts import render"],"id":3}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":0,"column":0},"end":{"row":0,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1564521771369,"hash":"1210bad0c61b1a2bd5de84e62bd58befb1e770c1"}